class User {
    name: string;
    age: number;
    active: boolean;

    constructor(name: string, age: number, active: boolean) {
        this.name = name;
        this.age = age;
        this.active = active;
    }

    canVote(): boolean {
        return this.age >= 18 && this.active;
    }

    greet(): string {
        return `Salve, ${this.name}!`;
    }
}

class UserManager {
    users: User[];

    constructor() {
        this.users = [
            new User("Marcus", 25, true),
            new User("Julia", 17, false),
            new User("Claudia", 42, true),
            new User("Augustus", 33, false)
        ];
    }

    processUsers(users: User[]): number {
        let count = 0;

