            return null;
        }

        let oldest = user;

        for (const user of users) {
            if (user.age > oldest.age) {
                oldest = user;
            }
        }

        return oldest;
    }

    safeDivide(a: number, b: number): number {
        if (b === 0) {
            throw new Error("Division by zero");
        }

        return a / b;
    }
}

