class User {
  name: string;
  age: number;
  active: boolean;

  constructor(name: string, age: number, active: boolean) {
    this.name = name;
    this.age = age;
    this.active = active;
  }

  canVote(): boolean {
    return this.age >= 18 && this.active;
  }

  greet(): string {
    return `Hello, ${this.name}!`;
  }
}

function validateAge(age: number): boolean {
  if (age < 0) return false;
  if (age > 150) return false;
  return true;
}

function processUsers(users: User[]): number {
  let count = 0;
  for (const user of users) {
    if (user.active) {
      console.log(user.greet());
      count++;
    }
  }
  return count;
}

function findOldest(users: User[]): User | null {
  if (users.length === 0) return null;
  let oldest = users[0];
  for (const user of users) {
    if (user.age > oldest.age) {
      oldest = user;
    }
  }
  return oldest;
}

function safeDivide(a: number, b: number): number {
  try {
